DROP TABLE IF EXISTS `books`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `books` (
  `id` int NOT NULL AUTO_INCREMENT,
  `title` varchar(45) NOT NULL,
  `summary` longtext,
  `translator` varchar(20) DEFAULT NULL,
  `sub_title` varchar(200) DEFAULT NULL,
  `description` longtext,
  `page` int unsigned DEFAULT NULL,
  `capacity` int DEFAULT NULL,
  `pub_date` date NOT NULL,
  `launched_date` date NOT NULL,
  `contents` longtext,
  `publisher_review` longtext,
  `image_url` varchar(2045) DEFAULT NULL,
  `purchase_url` varchar(2045) DEFAULT NULL,
  `author_id` int NOT NULL,
  `category_id` int NOT NULL,
  `publisher_id` int NOT NULL,
  PRIMARY KEY (`id`),
  KEY `books_author_id_c90d3b48_fk_authors_id` (`author_id`),
  KEY `books_category_id_1efdc3d3_fk_categories_id` (`category_id`),
  KEY `books_publisher_id_8625a46d_fk_publishers_id` (`publisher_id`),
  CONSTRAINT `books_author_id_c90d3b48_fk_authors_id` FOREIGN KEY (`author_id`) REFERENCES `authors` (`id`),
  CONSTRAINT `books_category_id_1efdc3d3_fk_categories_id` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`),
  CONSTRAINT `books_publisher_id_8625a46d_fk_publishers_id` FOREIGN KEY (`publisher_id`) REFERENCES `publishers` (`id`),
  CONSTRAINT `books_chk_1` CHECK ((`page` >= 0))
) ENGINE=InnoDB AUTO_INCREMENT=241 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
